    <script type="text/javascript">
        function loadJobs( store, position ) {
            jQuery.ajax({
                url: ajax_object.ajax_url,
                data: {
                    'action': 'get_jobs',
                    'store': store,
                    'position': position,
                },
                type: 'GET',
                success: function( response ){
                    jQuery('#recuitment-list table tbody').html(response);
                }

            })
        }

        loadJobs(-1);
        jQuery(document).ready(function(){
            jQuery('#action-find-jobs').on('click', function(e){
                e.preventDefault();
                var store = jQuery('#select-store').val();
                var position = jQuery('#select-position').val();
                loadJobs(store, position);
            });

            jQuery('#recuitment-list').on('click','.job-jd', function(e){
                e.preventDefault();
                var pid = jQuery(this).attr('data-pid');
                jQuery.ajax({
                    url: ajax_object.ajax_url,
                    data: {
                        'action': 'get_job_info',
                        'pid': pid
                    },
                    type: 'GET',
                    success: function( response ){
                        var modal = jQuery('#app-modal');
                        modal.find('.modal-body').html(response);
                        modal.find('.modal-title').html('Chức danh');
                        jQuery(modal).modal();
                    }

                })
            });

        })



    </script>

